import React, { useState } from 'react';
import './SubscribeSection.css';

const SubscribeSection = () => {
  const [formData, setFormData] = useState({
    email: '',
    date: '',
    tel: '',
    time: '',
    agree: false,
  });

  const [errors, setErrors] = useState({
    email: '',
    date: '',
    tel: '',
    time: '',
    agree: '',
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const fieldValue = type === 'checkbox' ? checked : value;
    setFormData((prevData) => ({ ...prevData, [name]: fieldValue }));
    setErrors((prevErrors) => ({ ...prevErrors, [name]: validateField(name, fieldValue) }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newErrors = {};
    for (const field in formData) {
      const error = validateField(field, formData[field]);
      if (error) {
        newErrors[field] = error;
      }
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      // Here goes your submission logic
      console.log('Form submitted:', formData);
    }
  };

  const validateField = (name, value) => {
    if (name === 'email') {
      if (value.trim() === '') {
        return 'Пожалуйста, заполните поле';
      }
      if (!/^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$/i.test(value)) {
        return 'Введите корректный email';
      }
    } else if (name === 'date') {
      if (value.trim() === '') {
        return 'Пожалуйста, выберите дату';
      }
      const year = parseInt(value.split('-')[0]);
      if (year < 1900 || year > 2020) {
        return 'Год должен быть от 1900 до 2020';
      }
    } else if (name === 'tel') {
      if (value.trim() === '') {
        return 'Пожалуйста, заполните поле';
      }
      if (!/^\+[0-9]{10,}$/.test(value)) {
        return 'Введите корректный номер телефона';
      }
    } else if (name === 'time' && value.trim() === '') {
      return 'Пожалуйста, выберите время';
    } else if (name === 'agree' && !value) {
      return 'Пожалуйста, согласитесь с обработкой персональных данных';
    }

    return '';
  };


  return (
    <section id="SubscribeSection" className="SubscribeSection">
      <div className="subscribe__info">
        <h2 className="subscribe__title"></h2>
        <p className="subscribe__text">
          Вся информация, которую ты&nbsp;оставишь на&nbsp;сайте
          или обсудишь с&nbsp;психологом, конфиденциальна.
        </p>

      </div>

      <form className="helpForm" onSubmit={handleSubmit}>
        <label htmlFor="email">
          <p className="inputCaption">Почта</p>
          <input
            className={`input ${errors.email && 'inputWrong'}`}
            type="email"
            name="email"
            id="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </label>
        {errors.email && <p className="error">{errors.email}</p>}

        <label htmlFor="date">
          <p className="inputCaption">Дата</p>
          <input
            className={`input ${errors.date && 'inputWrong'}`}
            type="date"
            name="date"
            id="date"
            value={formData.date}
            onChange={handleChange}
            required
          />
        </label>
        {errors.date && <p className="error">{errors.date}</p>}

        <label htmlFor="tel">
          <p className="inputCaption">Телефон</p>
          <input
            className={`input ${errors.tel && 'inputWrong'}`}
            type="tel"
            name="tel"
            id="tel"
            value={formData.tel}
            onChange={handleChange}
            required
          />
        </label>
        {errors.tel && <p className="error">{errors.tel}</p>}

        <label htmlFor="time">
          <p className="inputCaption">Время</p>
          <input
            className={`input ${errors.time && 'inputWrong'}`}
            type="time"
            name="time"
            id="time"
            value={formData.time}
            onChange={handleChange}
            required
          />
        </label>
        {errors.time && <p className="error">{errors.time}</p>}

        <label htmlFor="agree" className="checkboxLabel">
          <input
            type="checkbox"
            name="agree"
            id="agree"
            checked={formData.agree}
            onChange={handleChange}
          />
          Я согласен на обработку персональных данных
        </label>
        {errors.agree && <p className="error">{errors.agree}</p>}

        <button className="mainBtn" type="submit">
          Отправить заявку
        </button>
      </form>
    </section>
  );
};

export default SubscribeSection;
