import React from 'react';
import "./News.css"
import { news } from "./Data"
import NewsCard from '../NewsCard/NewsCard';

const News = () => {
  return (
    <div className="news">
      {news.map((item, index) => (
        <React.Fragment key={index}>
          <NewsCard newsItem={item} />
          {index === 2 && <NewsCard newsItem={news[2]} />} {/* Дополнительная карточка для индекса 2 */}
        </React.Fragment>
      ))}
    </div>
  );
};

export default News;
