import React, { useState } from "react";
import "./Header.css";
import { NavLink } from "react-router-dom";
import BurgerMenu from "../BurgerMenu/BurgerMenu";
import plus from '../../images/icons/Vector.svg'

function Header() {
    const [expanded1, setExpanded1] = useState(false);
    const [expanded2, setExpanded2] = useState(false);

    const toggleExpanded1 = () => {
        setExpanded1(!expanded1);
    };

    const toggleExpanded2 = () => {
        setExpanded2(!expanded2);
    };

    const handleKeyPress = (event, toggleExpanded) => {
        if (event.key === 'Enter' || event.key === ' ') {
            toggleExpanded();
        }
    };

    return (
        <div className="header">
            <nav className='header__nav'>
                <NavLink to="/" className="header__logo"></NavLink>

                <BurgerMenu />

            </nav>

            <div className="header__nav-links">

                <div className="header__nav-eleme">
                    <div className="faqQuestion">
                        <p className="header__nav-link">О нас</p>
                        <div
                            className={`toggle ${expanded1 ? 'expanded' : ''}`}
                            onClick={toggleExpanded1}
                            onKeyPress={(event) => handleKeyPress(event, toggleExpanded1)}
                            role="button"
                            tabIndex="0"
                        >
                            <img className="plus" src={plus} alt="plus" />
                        </div>
                    </div>
                    <div className={`faqAnswer ${expanded1 ? 'expandedAnswer' : ''}`}>
                        {expanded1 &&
                            <div className="header__aboutus">тут ссылки</div>
                        }
                    </div>

                </div>
                <NavLink to="/projects" className="header__nav-link">
                    Проекты
                </NavLink>
                <NavLink to="/news" className="header__nav-link">
                    Новости
                </NavLink>
                <NavLink to="/faq" className="header__nav-link">
                    FAQ
                </NavLink>
                <NavLink to="/contacts" className="header__nav-link">
                    Контакты
                </NavLink>
            </div>

            <div className="header__authbar">
                <NavLink to="/contactwithus" className='header__btn'>Связаться с нами</NavLink>
            </div>
        </div>
    );
}

export default Header;
