// BurgerMenu.js
import React, { useState } from "react";
import "./BurgerMenu.css";
import { NavLink, useLocation } from "react-router-dom";
import PopupForm from '../PopupForm/PopupForm';

function BurgerMenu() {
    const location = useLocation();
    const [isBurgerOpened, setIsBurgerOpened] = useState(false);
    const [popupVisible, setPopupVisible] = useState(false);

    const toggleBurgerMenu = () => {
        setIsBurgerOpened(!isBurgerOpened);
    };

    const openPopup = () => {
        setPopupVisible(true);
    };

    return (
        <section className='burger'>
            {popupVisible && <PopupForm onClose={() => setPopupVisible(false)} />}

            <div className="burger__menu">
                {isBurgerOpened ? (
                    <button
                        className="burger__close-button opened"
                        onClick={toggleBurgerMenu}
                    />
                ) : (
                    <button
                        className={`burger__button ${isBurgerOpened ? 'opened' : ''}`} // Adding the 'opened' class when the burger menu is opened
                        onClick={toggleBurgerMenu}
                    />
                )}
                {isBurgerOpened && (
                    <>
                        <ul className="burger__menu-list">
                            <li>
                                <NavLink
                                    to='/aboutus'
                                    onClick={toggleBurgerMenu}
                                    className={`burger__menu-item ${location.pathname === '/' ? 'burger__menu-item_underline' : ''}`}
                                >
                                    About Us
                                </NavLink>
                            </li>
                            <li>
                                <a
                                    href="https://500na700.ru/my/"
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className='header__nav-link'
                                >
                                    О 500на700
                                </a>
                            </li>
                            <li>
                                <NavLink
                                    to='/tools'
                                    onClick={toggleBurgerMenu}
                                    className={`burger__menu-item ${location.pathname === '/saved-movies' ? 'burger__menu-item_underline' : ''}`}
                                >
                                    Tools
                                </NavLink>
                            </li>
                            <li>
                                <NavLink
                                    to='/partner'
                                    onClick={toggleBurgerMenu}
                                    className={`burger__menu-item ${location.pathname === '/saved-movies' ? 'burger__menu-item_underline' : ''}`}
                                >
                                    Partner With Us
                                </NavLink>
                            </li>
                            <div className="header__authbar">
                                <button
                                    className='burger__contact-btn'
                                    onClick={openPopup}
                                >
                                    Связаться с нами
                                </button>
                            </div>
                        </ul>

                        <div className="burger-overlay" onClick={toggleBurgerMenu} />
                    </>
                )}
            </div>
        </section>
    );
}

export default BurgerMenu;
