import React from 'react';
import { useParams } from 'react-router-dom';
import { news } from '../../components/News/Data';
import './NewsPage.css'

const NewsPage = () => {
  const { id } = useParams();
  const index = parseInt(id, 10);
  const newsItem = news[index];

  console.log(newsItem);

  if (!newsItem) {
    return <div>Новость не найдена</div>;
  }

  const { image, title, text, caption,  } = newsItem;

  return (
    <div className='newspage'>
      <div className="newspage__image-block">
        <img src={image} alt={title} />
      </div>

      <div className="newspage__text-block">
        <h2 className="newspage__title">{title}</h2>
        <p className="newspage__caption">{caption}</p>
        <h3 className="newspage__subtitle">{subtitle}</h3>
        <p className="newspage__fulltext">{fulltext}</p>
      </div>
    </div>
  );
};

export default NewsPage;
