import React, { useState } from "react";
import "./Header.css";
import { NavLink } from "react-router-dom";
import BurgerMenu from "../BurgerMenu/BurgerMenu";
import arrow from '../../images/icons/Vector.svg'

function Header() {
    const [expanded1, setExpanded1] = useState(false);
    const [expanded2, setExpanded2] = useState(false);
    const [overlayVisible, setOverlayVisible] = useState(false);

    const toggleExpanded1 = () => {
        setExpanded1(!expanded1);
        setOverlayVisible(!overlayVisible); // Переключаем видимость оверлея
    };

    const toggleExpanded2 = () => {
        setExpanded2(!expanded2);
        setOverlayVisible(!overlayVisible); // Переключаем видимость оверлея
    };

    const handleKeyPress = (event, toggleExpanded) => {
        if (event.key === 'Enter' || event.key === ' ') {
            toggleExpanded();
        }
    };

    return (
        <div className="header">
            <nav className='header__nav'>
                <NavLink to="/" className="header__logo"></NavLink>
                <BurgerMenu />
            </nav>

            <div className="header__nav-links">
                <div className="header__expand-element">
                    <div className="header__element-before">
                        <div
                            className={`toggle ${expanded1 ? 'expanded' : ''}`}
                            onClick={toggleExpanded1}
                            // onKeyPress={(event) => handleKeyPress(event, toggleExpanded1)}
                            role="button"
                            tabIndex="0"
                        >
                            <p className="header__nav-link">О нас</p>
                            <img className="arrow" src={arrow} alt="arrow" />
                        </div>
                    </div>
                </div>
                <NavLink to="/projects" className="header__nav-link">
                    Проекты
                </NavLink>
                <NavLink to="/news" className="header__nav-link">
                    Новости
                </NavLink>
                <NavLink to="/faq" className="header__nav-link">
                    FAQ
                </NavLink>
                <NavLink to="/contacts" className="header__nav-link">
                    Контакты
                </NavLink>
            </div>

            <div className={`expanded-menu ${expanded1 ? 'expandedAnswer' : ''}`}>
                {expanded1 &&
                    <div className="header__aboutus">
                        <div className="header__aboutus-column">
                            <NavLink to="/aboutus" className="header__nav-link">
                                О 500 на 700
                            </NavLink>
                            <NavLink to="/docs" className="header__nav-link">
                                Документы
                            </NavLink>
                            <NavLink to="/plans" className="header__nav-link">
                                Устойчивое развитие
                            </NavLink>
                        </div>
                        <div className="header__aboutus-column">
                            <NavLink to="/team" className="header__nav-link">
                                Команда
                            </NavLink>
                            <NavLink to="/strategy" className="header__nav-link">
                                Стратегия
                            </NavLink>
                        </div>
                    </div>
                }
            </div>

            {overlayVisible && <div className="header-overlay" onClick={() => setOverlayVisible(false)}></div>}

            <div className="header__authbar">
                <NavLink to="/contactwithus" className='header__btn'>Связаться с нами</NavLink>
            </div>
        </div>
    );
}

export default Header;
