import { useState } from 'react';
import './Slider.css';
import { NavLink } from "react-router-dom";

function Slider({ images }) {
    const [current, setCurrent] = useState(0);

    const slideLeft = () => {
        setCurrent(current === 0 ? images.length - 1 : current - 1);
    }

    const slideRight = () => {
        setCurrent(current === images.length - 1 ? 0 : current + 1);
    }


    return (
        <div className='slider__container'>
            <div className="slider__info">
                <NavLink to="/contactwithus" className='main__btn'>Связаться с нами</NavLink>
                <h1 className="slider__title">Заголо вок 1 Заголо вок 1 Заголо вок 1</h1>
                <p className='slider__subtitle'>Описание проекта Описание проекта
                    Описание проекта Описание проекта Описание проекта Описание проекта
                    Описание проекта Описание проекта
                </p>
                <div className="slider__arrows">
                    <div className="slider__arrow-left" onClick={slideLeft}>ХУЙ</div>
                    <div className="slider__arrow-right" onClick={slideRight}>ХУЙ</div>
                </div>

            </div>
            <div className='slider'>
                <div className='slider__wrapper'>
                    {images.map((image, index) => {
                        return (
                            <div className={
                                index == current
                                    ? "slider__card slider__card-active"
                                    : "slider__card"
                            }
                                key={index}
                            >
                                <img className='card__image' src={image.image} alt='' />
                                <div className="card__overlay"></div>
                                <h2 className="card__title"></h2>
                            </div>
                        );
                    })}
                </div>
            </div>
        </div>
    );
}

export default Slider
