import React, { useState } from 'react';
import './PopupForm.css';

const PopupForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    tel: '',
    agree: false,
  });

  const [errors, setErrors] = useState({
    email: '',
    tel: '',
    agree: '',
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const fieldValue = type === 'checkbox' ? checked : value;
    setFormData((prevData) => ({ ...prevData, [name]: fieldValue }));
    setErrors((prevErrors) => ({ ...prevErrors, [name]: '' }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newErrors = {};
    for (const field in formData) {
      const error = validateField(field, formData[field]);
      if (error) {
        newErrors[field] = error;
      }
    }

    setErrors(newErrors);

    if (Object.keys(newErrors).length === 0) {
      console.log('Form submitted:', formData);
    }
  };

  const validateField = (name, value) => {
    if (name === 'email') {
      if (value.trim() === '') {
        return 'Пожалуйста, заполните поле';
      }
      if (!/^[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$/i.test(value)) {
        return 'Введите корректный email';
      }
    } else if (name === 'tel' && !/^\+?\d{5,11}$/.test(value)) {
      return 'Введите корректный номер (от 5 до 11 цифр)';
    } else if (name === 'agree' && !value) {
      return 'Пожалуйста, согласитесь с обработкой персональных данных';
    }

    return '';
  };

  return (
    <section id="PopupForm" className="PopupForm">
      <form className="popup__form" onSubmit={handleSubmit} noValidate>
        <h1 className="popup__title">CВЯЗАТЬСЯ С НАМИ</h1>
        <label htmlFor="email">
          <input
            className={`input ${errors.email && 'inputWrong'}`}
            type="email"
            name="email"
            id="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Электронная почта"
            required
          />
          {errors.email && <span className="error">{errors.email}</span>}
        </label>

        <label htmlFor="tel">
          <input
            className={`input ${errors.tel && 'inputWrong'}`}
            type="tel"
            name="tel"
            id="tel"
            value={formData.tel}
            onChange={handleChange}
            placeholder="Телефон"
            required
          />
          {errors.tel && <span className="error">{errors.tel}</span>}
        </label>

        <label className="custom-checkbox">
          <input
            type="checkbox"
            name="agree"
            id="agree"
            checked={formData.agree}
            onChange={handleChange}
          />
          <span className="checkmark"></span>
        </label>
        <p className="checkbox__caption">Я согласен на обработку персональных данных</p>
        {errors.agree && <span className="error">{errors.agree}</span>}

        <button className="mainBtn" type="submit">
          Отправить
        </button>
      </form>
    </section>
  );
};

export default PopupForm;
